public class OrderProcessor {
    public OrderData processOrder(String orderString, Map<String, Integer> priceList) {
        String[] orderData = orderString.split("\\s+");
        String productKey = orderData[0].split("–")[1];
        Integer productPrice = priceList.get(productKey);
        Integer quantity = Integer.parseInt(orderData[1]);

        return new OrderData(quantity, productPrice);
    }

    public int calculateOrderPrice(OrderData orderData) {
        return orderData.getQuantity() * orderData.getProductPrice();
    }

    public static class OrderData {
        private final int quantity;
        private final int productPrice;

        public OrderData(int quantity, int productPrice) {
            this.quantity = quantity;
            this.productPrice = productPrice;
        }

        public int getQuantity() {
            return quantity;
        }

        public int getProductPrice() {
            return productPrice;
        }
    }
}
```

