    double basePrice = order.quantity * order.itemPrice;
    double discount = calculateDiscount(order.quantity, order.itemPrice);
    double maxAdditionalDiscount = Math.min(basePrice * 0.1, 100);
    return basePrice - discount + maxAdditionalDiscount;
}

